import { UserContext } from '../context'
import { User, Credential } from '../types'
import { useState } from 'react'
import { useLocalStorage } from '../hooks'
import { post, setToken } from '../utils/http'


type UserProviderProps = {
    children: React.ReactNode
  }

  export default function UserProvider({ children }: UserProviderProps) {
    const [user, setUser] = useLocalStorage<User | undefined>('user',undefined)

    if (user){
      setToken(user.token)
  }

  const login = async (username: string, password: string) => {
    try {
    const user = await post<Credential, User>('/api/auth/login', {
    username,
    password
    })
    setUser(user)
    setToken(user.token)
    return true
    } catch(error){
        if (error instanceof Error) {
            return error.message
        }
        return 'Unable to login right now, please try again!'
    }  
}

const register = async (username: string, password: string) => {
  try {
  const user = await post<Credential, User>('/api/auth/register', {
  username,
  password
  })
  setUser(user)
  setToken(user.token)
  return true
  } catch(error){
      if (error instanceof Error) {
          return error.message
      }
      return 'Unable to register right now, please try again!'
  }  
}


    const logout = () => setUser(undefined)


    return (
        <UserContext.Provider value={{ user, login,register,logout }}>
        {children}
      </UserContext.Provider>
  )
}
