import { useContext, useState, useReducer } from 'react'
import { Navigate, useNavigate, useLocation } from 'react-router-dom'
import { UserContext } from '../context'
import { Tile, Button } from '../components'
import { TILE_STATUS, PLAYERACTIONTYPE } from '../constants'
import { useLocalStorage } from '../hooks'

import style from './MainGame.module.css'
import { type } from 'os'


type SelectAction = {

    type: PLAYERACTIONTYPE
    payload: number

}

function bActionReducer(state: number[], action: SelectAction) {
    const { type, payload } = action
    switch (type) {
      case PLAYERACTIONTYPE.SELECT_BLACK:
        return [...state, payload]
      default:
        return state
    }
  }

  function wActionReducer(state: number[], action: SelectAction) {
    const { type, payload } = action
    switch (type) {
      case PLAYERACTIONTYPE.SELECT_WHITE:
        return [...state, payload]
      default:
        return state
    }
  }




export default function MainGame() {




    const [bSelectedTiles, bDispatch] = useReducer(bActionReducer, [])
    const [wSelectedTiles, wDispatch] = useReducer(wActionReducer, [])
    const [selectedGamesBlackMoves, saveBlackMoves] = useLocalStorage<Record<string, any[]>>('blackMoves', {})
    const [selectedGamesWhiteMoves, saveWhiteMoves] = useLocalStorage<Record<string, any[]>>('whiteMoves', {})

    const gameID = Object.keys(selectedGamesBlackMoves).length + 1



    //const gameID = selectedGamesBlackMoves

    const handleConfirmClick = async () => {



        saveBlackMoves({... selectedGamesBlackMoves, ['Game:'+gameID]:bSelectedTiles})
        saveWhiteMoves({... selectedGamesWhiteMoves, ['Game:'+gameID]:wSelectedTiles})
        console.log("Black Moves: ",bSelectedTiles)
        console.log("White Moves: ",wSelectedTiles)
        console.log(gameID)
        console.log(checkIfWinner(bSelectedTiles))


    }

    //const { size } = 9
  return (

    <div className={style.gameSpace}>

        <div className={style.tile} style={{ gridTemplateColumns: `repeat(${parseInt("6")}, 1fr)`}}>
        {[...Array(parseInt('6') * parseInt('6'))].map((_, index) => (
            <Tile key={`tile-${index}`} id={index} onSelectBlack={() => bDispatch({ type: PLAYERACTIONTYPE.SELECT_BLACK, payload: index})} onSelectWhite={() => wDispatch({ type: PLAYERACTIONTYPE.SELECT_WHITE, payload: index})} moveNumber={bSelectedTiles.length + wSelectedTiles.length}  />))}


            </div>


      <button className={style.button} onClick={handleConfirmClick}>
        Save Game
      </button>


</div>

  )
}
// SET NUMBER OF ROWS AND COLUMNS HERE.
let rows: number = 9;
let cols: number = 20;
// Various check for winner functions.
function checkIfWinner(board: Array<number>) {
    // Horizontal 
    for (let j = 0; j < cols*(rows-1); j=j+cols) {

      for (let i = 0; i < cols-4; i++) {
        if (
            board.includes(j+i) &&
            board.includes(j+i+1) &&
            board.includes(j+i+2) &&
            board.includes(j+i+3) &&
            board.includes(j+i+4)
        ) {
          return true
        }
      }
    }



    // Vertical 
    for (let j = 0; j < cols; j++) {

      for (let i = 0; i < rows*(cols-4); i=i+cols) {

        if (
            board.includes(j+i) &&
            board.includes(j+i+cols) &&
            board.includes(j+i+2*cols) &&
            board.includes(j+i+3*cols) &&
            board.includes(j+i+4*cols)
        ) {
          return true
        }
      }
    }

    // Diagonal down 
    for (let j = 0; j < cols-4; j++) {

        for (let i = 0; i < rows*(cols-4); i=i+cols) {


          if (
              board.includes(j+i) &&
              board.includes(j+1+(i+cols)) &&
              board.includes(j+2+(i+cols*2)) &&
              board.includes(j+3+(i+cols*3)) &&
              board.includes(j+4+(i+cols*4))
          ) {
            return true
          }
        }
      }  

          // Diagonal up 
    for (let j = 0; j < cols-4; j++) {



        for (let i = 4*cols; i < rows*(cols); i=i+cols) {



          if (
              board.includes(j+i) &&
              board.includes(j+1+(i-cols)) &&
              board.includes(j+2+(i-cols*2)) &&
              board.includes(j+3+(i-cols*3)) &&
              board.includes(j+4+(i-cols*4))
          ) {
            return true
          }
        }
      }  

    return false
  }
