import { useContext, useState, useReducer } from 'react'
import { Navigate, useNavigate, useLocation, useParams } from 'react-router-dom'
import { UserContext } from '../context'
import { Tile, Button } from '../components'
import { TILE_STATUS, PLAYERACTIONTYPE } from '../constants'
import { useLocalStorage } from '../hooks'

import style from './MainGame.module.css'
import { type } from 'os'



type SelectAction = {

    type: PLAYERACTIONTYPE
    payload: number

}

function bActionReducer(state: number[], action: SelectAction) {
    const { type, payload } = action
    switch (type) {
      case PLAYERACTIONTYPE.SELECT_BLACK:
        return [...state, payload]
      default:
        return state
    }
  }

  function wActionReducer(state: number[], action: SelectAction) {
    const { type, payload } = action
    switch (type) {
      case PLAYERACTIONTYPE.SELECT_WHITE:
        return [...state, payload]
      default:
        return state
    }
  }




export default function MainGame() {

    const { boardSize } = useParams()

    let bs = 0    

    if (boardSize !== undefined) {

        bs = parseInt(boardSize)


    } else {

        bs = 5


    }


    const navigate = useNavigate()


    let [bSelectedTiles, bDispatch] = useReducer(bActionReducer, [])
    const [wSelectedTiles, wDispatch] = useReducer(wActionReducer, [])
    const [selectedGamesBlackMoves, saveBlackMoves] = useLocalStorage<Record<string, number[]>>('blackMoves', {})
    const [selectedGamesWhiteMoves, saveWhiteMoves] = useLocalStorage<Record<string, number[]>>('whiteMoves', {})
    const [selectedGamesBoardSize, saveBoardSize] = useLocalStorage<Record<string, number>>('boardSize', {})
    const [selectedGamesWinner, saveBoardWinner] = useLocalStorage<Record<string, string>>('result', {})


    const moveNumber = bSelectedTiles.length + wSelectedTiles.length

    let res = "Black Turn"
    let result = ""
    let gameOver: boolean = false;

    if(checkIfWinner(bSelectedTiles,bs)) {
        res = 'Black Wins'
        result = 'black'
        gameOver = true
    }
    else if(checkIfWinner(wSelectedTiles,bs)) {
        res = 'White Wins'
        result = 'white'
        gameOver = true
    } else if(moveNumber === bs*bs){
        res = 'Draw'
        result = 'draw'
        gameOver = true

    }
    else {

        if (moveNumber % 2 === 0 ) res = 'Black Turn'
        else res = 'White Turn'

    }







    const gameID = Object.keys(selectedGamesBlackMoves).length + 1



    const resetBoard = () => {

        navigate('/')

      }

    const handleConfirmClick = async () => {

        if(gameOver){



        saveBlackMoves({... selectedGamesBlackMoves, ['Game:'+gameID]:bSelectedTiles})
        saveWhiteMoves({... selectedGamesWhiteMoves, ['Game:'+gameID]:wSelectedTiles})
        saveBoardSize({... selectedGamesBoardSize, ['Game:'+gameID]:bs})
        saveBoardWinner({... selectedGamesWinner, ['Game:'+gameID]:result})


        }

        navigate(`/home`)




    }


  return (


    <div className={style.gameSpace}>

        <div className={style.title}> 

        <div className={style.score}> {res} </div>

         </div>

         <div className={style.row}> 
        <div className={style.tile} style={{ gridTemplateColumns: `repeat(${bs}, 2rem)`}}>
        {[...Array(bs * bs)].map((_, index) => (
            <Tile key={`tile-${index}`} id={index} onSelectBlack={() => bDispatch({ type: PLAYERACTIONTYPE.SELECT_BLACK, payload: index})} onSelectWhite={() => wDispatch({ type: PLAYERACTIONTYPE.SELECT_WHITE, payload: index})} moveNumber={bSelectedTiles.length + wSelectedTiles.length} gameOver={ gameOver} />))}


            </div>
             </div>

            <div className={style.buttonContainer}> 
      <Button type='submit' onClick = {()=> {resetBoard()}}>Reset</Button>
      <Button type='submit' onClick={handleConfirmClick}>
        Leave
      </Button>
      </div>


</div>

  )
}

// Various check for winner functions.
function checkIfWinner(board: Array<number>, bs: number) {

    // SET NUMBER OF ROWS AND COLUMNS HERE.
let rows: number = bs;
let cols: number = bs;

    // Horizontal 
    for (let j = 0; j < cols*(rows-1); j=j+cols) {

      for (let i = 0; i < cols-4; i++) {
        if (
            board.includes(j+i) &&
            board.includes(j+i+1) &&
            board.includes(j+i+2) &&
            board.includes(j+i+3) &&
            board.includes(j+i+4)
        ) {
          return true
        }
      }
    }



    // Vertical 
    for (let j = 0; j < cols; j++) {

      for (let i = 0; i < rows*(cols-4); i=i+cols) {

        if (
            board.includes(j+i) &&
            board.includes(j+i+cols) &&
            board.includes(j+i+2*cols) &&
            board.includes(j+i+3*cols) &&
            board.includes(j+i+4*cols)
        ) {
          return true
        }
      }
    }

    // Diagonal down 
    for (let j = 0; j < cols-4; j++) {

        for (let i = 0; i < rows*(cols-4); i=i+cols) {


          if (
              board.includes(j+i) &&
              board.includes(j+1+(i+cols)) &&
              board.includes(j+2+(i+cols*2)) &&
              board.includes(j+3+(i+cols*3)) &&
              board.includes(j+4+(i+cols*4))
          ) {
            return true
          }
        }
      }  

          // Diagonal up 
    for (let j = 0; j < cols-4; j++) {



        for (let i = 4*cols; i < rows*(cols); i=i+cols) {



          if (
              board.includes(j+i) &&
              board.includes(j+1+(i-cols)) &&
              board.includes(j+2+(i-cols*2)) &&
              board.includes(j+3+(i-cols*3)) &&
              board.includes(j+4+(i-cols*4))
          ) {
            return true
          }
        }
      }  

    return false
  }
