    id: number
    onSelectBlack: () => void
    onSelectWhite: () => void
    moveNumber: number


}

export default function Tile(props: TileProps) {
    const { id, onSelectBlack, onSelectWhite  } = props 
    const [status, setStatus] = useState(TILE_STATUS.AVAILABLE)

    let turn: number = 1

        if(status === "BLACK" || status === "WHITE"){
            turn++;
            console.log(turn)
        }



    const getClassName = () => {

        const className = style.tile
        if (status !== null && status !== undefined){

        switch(status) {
            case TILE_STATUS.AVAILABLE:
                return `${className} ${style.available}`
