import { useParams } from "react-router-dom"
import { useLocalStorage } from '../hooks'
import { Tile, Button } from '../components'
import { Navigate, useNavigate, useLocation } from 'react-router-dom'
import { TILE_STATUS, PLAYERACTIONTYPE } from '../constants'
import { useContext, useState, useReducer } from 'react'

import style from './GameHistory.module.css'
import { type } from 'os'


type SelectAction = {

    type: PLAYERACTIONTYPE
    payload: number

}

export default function GameHistory() {

    const { gameID } = useParams()
    const [games] = useLocalStorage<Record<string, number[]>>('games', {})
    const navigate = useNavigate()

    const [bSelectedTiles, bDispatch] = useReducer(bActionReducer, [games[gameID]])
    const [wSelectedTiles, wDispatch] = useReducer(wActionReducer, [])


    if(!gameID) return null

    //console.log(gameID)

    //console.log(games["Game:2"])

    console.log(games[gameID])


    //console.log(gameID)


    function bActionReducer(state: number[], action: SelectAction) {
        const { type, payload } = action
        switch (type) {
          case PLAYERACTIONTYPE.SELECT_BLACK:
            return [...state, payload]
          default:
            return state
        }
      }

      function wActionReducer(state: number[], action: SelectAction) {
        const { type, payload } = action
        switch (type) {
          case PLAYERACTIONTYPE.SELECT_WHITE:
            return [...state, payload]
          default:
            return state
        }
      }



  return (
    <div className={style.container}>
      <>
      <h1 className={style.header}>
        Game Details
      </h1>
      <div className={style.container}>
        <div className={style.title}>
              Result: add later

      </div>
      <div>

      <div className={style.container_game} style={{ gridTemplateColumns: `repeat(${6}, 1fr)`}}>
      {[...Array(6 * 6)].map((_, index) => (
      <Tile key={`tile-${index}`} id={index} onSelectBlack={() => bDispatch({ type: PLAYERACTIONTYPE.SELECT_BLACK, payload: index})} onSelectWhite={() => wDispatch({ type: PLAYERACTIONTYPE.SELECT_WHITE, payload: index})} moveNumber={bSelectedTiles.length + wSelectedTiles.length}  />

        ))}
      </div>
      <div className={style.button}>
        <p>    <Button type="submit" onClick={ () => navigate(`/games`) }>Back</Button></p>

      </div>
      </div>
      </div>
      </>
      </div>

  )
}
